import { useEffect, useRef } from 'react';
import * as faceapi from 'face-api.js';
import idCardImage from "./images/Vishnu_ID_Card.png"; // Importing image
import selfieImage from "./images/selfie-without-specs.jpg"; // Importing image

function App() {
  const idCardRef = useRef();
  const selfieRef = useRef();
  const isFirstRender = useRef(true);
  const [matchResult, setMatchResult] = useState("");


  const renderFace = async (image, x, y, width, height) => {
    const canvas = document.createElement("canvas");
    canvas.width = width;
    canvas.height = height;
    const context = canvas.getContext("2d");

    context?.drawImage(image, x, y, width, height, 0, 0, width, height);
    canvas.toBlob((blob) => {
      image.src = URL.createObjectURL(blob);
    }, "image/jpeg");
  };

  useEffect(() => {
    // Prevent the function from executing on the first render
    if (isFirstRender.current) {
      isFirstRender.current = false; // toggle flag after first render/mounting
      return;
    }

    (async () => {
      // loading the models
      await faceapi.nets.ssdMobilenetv1.loadFromUri("/models");
      await faceapi.nets.tinyFaceDetector.loadFromUri("/models");
      await faceapi.nets.faceLandmark68Net.loadFromUri("/models");
      await faceapi.nets.faceRecognitionNet.loadFromUri("/models");
      await faceapi.nets.faceExpressionNet.loadFromUri("/models");

      // detect a single face from the ID card image
      const idCardFaceDetection = await faceapi
        .detectSingleFace(
          idCardRef.current,
          new faceapi.TinyFaceDetectorOptions()
        )
        .withFaceLandmarks()
        .withFaceDescriptor();

      // detect a single face from the selfie image
      const selfieFaceDetection = await faceapi
        .detectSingleFace(
          selfieRef.current,
          new faceapi.TinyFaceDetectorOptions()
        )
        .withFaceLandmarks()
        .withFaceDescriptor();

      //(OPTIONAL)
      /**
       * If a face was detected from the ID card image,
       * call our renderFace() method to display the detected face.
       */
      if (idCardFaceDetection) {
        const { x, y, width, height } = idCardFaceDetection.detection.box;
        renderFace(idCardRef.current, x, y, width, height);
      }
      //(OPTIONAL)
      /**
       * If a face was detected from the selfie image,
       * call our renderFace() method to display the detected face.
       */
      if (selfieFaceDetection) {
        const { x, y, width, height } = selfieFaceDetection.detection.box;
        renderFace(selfieRef.current, x, y, width, height);
      }


     // Compare faces
     if (idCardFaceDetection && selfieFaceDetection) {
      const distance = faceapi.euclideanDistance(
        idCardFaceDetection.descriptor,
        selfieFaceDetection.descriptor
      );

      const threshold = 0.6; // Adjust this value as needed
      const match = distance < threshold;

      setMatchResult(match ? "The faces match!" : "The faces do not match.");

      // // Render detected faces (optional)
      // renderFace(idCardRef.current, idCardFaceDetection.detection.box);
      // renderFace(selfieRef.current, selfieFaceDetection.detection.box);
    } else {
      setMatchResult("Could not detect faces in both images.");
    }



    })();
  }, []);

  return (
    <>
      <div className="gallery">
        <img
          ref={idCardRef}
          src={idCardImage}
          alt="ID card"
          style={{ height: "auto" }}
        />
      </div>

      <div className="gallery">
        <img
          ref={selfieRef}
          src={selfieImage}
          alt="Selfie"
          style={{ height: "auto" }}
        />
      </div>
      <div className="match-result">{matchResult}</div>
    </>
  );
}

export default App;
